BEGIN;

DROP TABLE IF EXISTS
"names",
"adjectives",
"verbs",
"complements"
CASCADE;


CREATE TABLE "names" (
    "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, 
    "name" VARCHAR (50)
);

CREATE TABLE "adjectives" (
    "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, 
    "name" VARCHAR (50)
);

CREATE TABLE "verbs" (
    "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, 
    "name" VARCHAR (50)
);

CREATE TABLE "complements" (
    "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, 
    "name" VARCHAR (50)
);





COMMIT;
